INSERT INTO `sub_paragraphe` (`id`, `numero`, `id_paragraphe`, `libelle`, `comment`) VALUES
(1, 1, 1, 'Politique de la sécurité de l\'information', 'Apporter à la sécurité de l\'information une orientation et un soutien de la part de la direction, conformément aux exigences métier et aux lois et règlements en vigueur.'),
(2, 1, 2, 'Organisation interne', 'Gérer la sécurité de l\'information au sein de l\'organisme'),
(3, 2, 2, 'Tiers', 'Assurer la sécurité de l\'information et des moyens de traitement de l\'information appartenant à l\'organisme et consultés, traités, communiqués ou gérés par des tiers.'),
(4, 1, 3, 'Responsabilités relatives aux actifs', 'Mettre en place et maintenir une protection appropriée des actifs de l\'organisme.'),
(5, 2, 3, 'Classification des informations', 'Garantir un niveau de protection approprié aux informations.'),
(6, 1, 4, 'Avant le recrutement', 'Garantir que les salariés, contractants et utilisateurs tiers connaissent leurs responsabilités et qu’ils conviennent pour les fonctions qui leur sont attribuées et réduire le risque de vol, de fraude ou de mauvais usage des équipements.'),
(7, 2, 4, 'Pendant la durée du contrat', 'Veiller à ce que tous les salariés, contractants et utilisateurs tiers soient conscients des menaces pesant sur la sécurité de l’information, de leurs responsabilités financières ou autres, et disposent des éléments requis pour prendre en charge la politique de sécurité de l’organisme dans le cadre de leur activité normale et réduire le risque d’erreur humaine.'),
(8, 3, 4, 'Fin ou modification du contrat', 'Veiller à ce que les salariés, contractants et utilisateurs tiers quittent un organisme ou changent de poste selon une procédure définie.'),
(9, 1, 5, 'Zones sécurisées', 'Empêcher tout accès physique non autorisé, tout dommage ou intrusion dans les locaux ou portant sur les informations de l’organisme.'),
(10, 2, 5, 'Sécurité du matériel', 'Empêcher la perte, l’endommagement, le vol ou la compromission des actifs et l’interruption des activités de l’organisme.'),
(11, 1, 6, 'Procédures et responsabilités liées à l\'exploitation', 'Assurer l\'exploitation correcte et sécurisée des moyens de traitement de l\'information.'),
(12, 2, 6, 'Gestion de la prestation de service conclus avec un tiers', 'Mettre en oeuvre et maintenir un niveau de sécurité de l’information et de service adéquat et conforme aux accords de prestation de service conclus avec un tiers.'),
(13, 3, 6, 'Planification et acceptation du système', 'Réduire le plus possible le risque de pannes du système.'),
(14, 4, 6, 'Protection contre les codes malveillants et mobiles', 'Protéger l\'intégrité des logiciels et de l\'information.'),
(15, 5, 6, 'Sauvegarde', 'Maintenir l\'intégrité et la disponibilité des informations et des moyens de traitement de l\'information.'),
(16, 6, 6, 'Gestion de la sécurité des réseaux', 'Assurer la protection des informations sur les réseaux et la protection de l’infrastructure sur laquelle elles s’appuient.'),
(17, 7, 6, 'Manipulation des supports', 'Empêcher la divulgation, la modification, le retrait ou la destruction non autorisé(e) d\'actifs et l’interruption des activités de l’organisme.'),
(18, 8, 6, 'Echange des informations', 'Maintenir la sécurité des informations et des logiciels échangés au sein de l’organisme et avec une entité extérieure.'),
(19, 9, 6, 'Services de commerce électronique', 'Assurer la sécurité des services de commerce électronique, ainsi que leur utilisation sécurisée.'),
(20, 10, 6, 'Surveillance', 'Détecter les traitements non autorisés de l\'information.'),
(21, 1, 7, 'Exigences métier relatives au contrôle d\'accès', 'Maîtriser l\'accès à l\'information.'),
(22, 2, 7, 'Gestion des accès des utilisateurs', 'Contrôler l’accès des utilisateurs autorisés et empêcher les accès non autorisés aux systèmes d’information.'),
(23, 3, 7, 'Responsabilités des utilisateurs', 'Empêcher l’accès d’utilisateurs non habilités et la compromission ou le vol d’informations et de moyens de traitement de l’information.'),
(24, 4, 7, 'Contrôle d\'accès réseau', 'Empêcher les accès non autorisés aux services disponibles sur le réseau.'),
(25, 5, 7, 'Contrôle d\'accès au système d\'exploitation', 'Empêcher les accès non autorisés aux systèmes d\'exploitation.'),
(26, 6, 7, 'Contrôle d\'accès aux applications et à l\'information', 'Empêcher les accès non autorisés aux informations stockées dans les applications.'),
(27, 7, 7, 'Informatique mobile et télétravail', 'Garantir la sécurité de l’information lors de l’utilisation d’appareils informatiques mobiles et d’équipements de télétravail.'),
(28, 1, 8, 'Exigences de sécurité applicables aux systèmes d\'information', 'Veiller à ce que la sécurité fasse partie intégrante des systèmes d’information.'),
(29, 2, 8, 'Bon fonctionnement des applications', 'Empêcher toute erreur, perte, modification non autorisée ou tout mauvais usage des informations dans les applications.'),
(30, 3, 8, 'Mesures cryptographiques', 'Protéger la confidentialité, l’authenticité ou l’intégrité de l’information par des moyens cryptographiques.'),
(31, 4, 8, 'Sécurité des fichiers système', 'Garantir la sécurité des fichiers système.'),
(32, 5, 8, 'Sécurité en matière de développement et d\'assistance technique', 'Garantir la sécurité du logiciel et des informations d’application.'),
(33, 6, 8, 'Gestion des vulnérabilités techniques', 'Réduire les risques liés à l’exploitation des vulnérabilités techniques ayant fait l’objet d’une publication.'),
(34, 1, 9, 'Remontée des évènements et des failles liés à la sécurité de l\'information', 'Garantir que le mode de notification des événements et failles liés à la sécurité de l’information permet la mise en oeuvre d’une action corrective, dans les meilleurs délais.'),
(35, 2, 9, 'Gestion des incidents liés à la sécurité de l\'information et des améliorations', 'Garantir la mise en place d’une approche cohérente et efficace pour la gestion des incidents liés à la sécurité de l’information.'),
(36, 1, 10, 'Gestion de la continuité de l’activité d’un point de vue aspects de la sécurité de l\'information', 'Empêcher les interruptions des activités de l’organisme, protéger les processus métier cruciaux des effets causés par les défaillances majeures des systèmes d’information ou par des sinistres et garantir une reprise de ces processus dans les meilleurs délais.'),
(37, 1, 11, 'Conformité aux exigences légales', 'Eviter toute violation des obligations légales, statutaires, réglementaires ou contractuelles et des exigences de sécurité.'),
(38, 2, 11, 'Conformité avec les politiques et normes de sécurité et conformité technique', 'S’assurer de la conformité des systèmes avec les politiques et normes de sécurité de l’organisme.'),
(39, 3, 11, 'Prises en compte de l\'audit du système d\'information', 'Optimiser l’efficacité et réduire le plus possible l’interférence avec le/du processus d’audit du système d’information.');
